import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Search from './Search'

class App extends Component {
  constructor() {
    super()
    this.state = {
      searchValue: '',
      searchResults: ''
    }
  }

  updateSearch = (event) => {
    if (event.target !== null){
    this.setState({
      searchValue: event.target.value
    })
  }
    fetch(`https://cors-anywhere.herokuapp.com/http://gen.lib.rus.ec/search.php?req=${event.target.value}&lg_topic=libgen&open=0&view=simple&res=25&phrase=1&column=def`)
    .then((response) => response.text())
    .then((data) => {
      if (event.target.value === this.state.searchValue)
      this.setState({
        searchResults: data
      })
    })
  }


  render() {
    return (
      <div className="App">
        <Search updateSearch={this.updateSearch}/>
        <p>'raw html lol'{this.state.searchResults}</p>
      </div>
    );
  }
}

export default App;
