import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Search from './Search'
import Results from './Results'

class App extends Component {
  constructor() {
    super()
    this.state = {
      searchValue: '',
      searchResults: ''
    }
  }

  updateSearch = (event) => {
    fetch(`https://cors-anywhere.herokuapp.com/http://gen.lib.rus.ec/search.php?req=${event.target.value}&phrase=1&view=simple&column=def&sort=title&sortmode=DESC`)
    .then((response) => response.text())
    .then((data) => {

      var parser = new DOMParser();

      // Parse the text
      var doc = parser.parseFromString(data, "text/html");
      let list = doc.querySelectorAll('[title = "Gen.lib.rus.ec"]')
  let list2 = doc.querySelectorAll('[href *= "book/index.php?md5="]')
      let listUpdated =[]
      for (let i = 0; i < list.length; i++){
        if (list[i].title === 'Gen.lib.rus.ec'){
          listUpdated.push((i+1) + '.' + list2[i].innerText + ': ' + list[i].getAttribute('href'))
        }
      }
      this.setState({
        searchResults: listUpdated.join('\n')
      })
      //console.log(listUpdated.join('\n'))
    })
  }


  render() {
    return (
      <div className="App">
        <Search updateSearch={this.updateSearch}/>
        <Results searchResults = {this.state.searchResults}/>
      </div>
    );
  }
}

export default App;
