import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Search from './Search'

class App extends Component {
  constructor() {
    super()
    this.state = {
      searchValue: '',
      searchResults: ''
    }
  }

  updateSearch = (event) => {
    fetch(`https://cors-anywhere.herokuapp.com/http://gen.lib.rus.ec/search.php?req=${event.target.value}&lg_topic=libgen&open=0&view=simple&res=25&phrase=1&column=def`)
    .then((response) => response.text())
    .then((data) => {

      var parser = new DOMParser();

      // Parse the text
      var doc = parser.parseFromString(data, "text/html");
      let list = doc.querySelectorAll('[title = "Gen.lib.rus.ec"]')
      let listUpdated =[]
      for (let i = 0; i < list.length; i++){
        if (list[i].title === 'Gen.lib.rus.ec'){
          listUpdated.push(list[i].getAttribute('href'))
        }
      }
      this.setState({
        searchResults: listUpdated
      })
      console.log(listUpdated)
    })
  }


  render() {
    return (
      <div className="App">
        <Search updateSearch={this.updateSearch}/>
        <p>'raw html lol'{this.state.searchResults}</p>
      </div>
    );
  }
}

export default App;
